import React, { Component } from "react";

import Habits from "./components/habits";
import HabitBar from "./components/habitBar";
import HarbitInput from "./components/harbitInput";

import "./app.css";

class app extends Component {
  state = {
    habits: [
      { id: 1, name: "Reading", count: 0 },
      { id: 2, name: "Running", count: 0 },
      { id: 3, name: "Coding", count: 0 },
    ],
  };
  handleIncrement = (habit) => {
    const newHabit = [...this.state.habits];
    const index = newHabit.indexOf(habit);
    newHabit[index].count++;
    this.setState({ newHabit });
  };

  handleDecrement = (habit) => {
    const newHabit = [...this.state.habits];
    const index = newHabit.indexOf(habit);
    const count = newHabit[index].count - 1;
    newHabit[index].count = count < 0 ? 0 : count;
    this.setState({ newHabit });
    console.log(newHabit);
  };

  handleDelete = (habit) => {
    const newHabit = this.state.habits.filter((item) => item.id !== habit.id);
    this.setState({ habits: newHabit }); //객체의 키 이름과 변수명이 다르다!!!!
  };
  render() {
    return (
      <>
        <div className="bar-bgcolor">
          <div className="bar">
            <span className="leaf">
              <i className="fa-solid fa-leaf"></i>
            </span>
            <span className="bar-name">Habit Tracker</span>
            <span className="habit-count all">8</span>
          </div>
        </div>
        <HarbitInput />
        <Habits
          habits={this.state.habits}
          onIncrement={this.handleIncrement}
          onDecrement={this.handleDecrement}
          onDelete={this.handleDelete}
        />
        ;
      </>
    );
  }
}
export default app;
